const APP_PORT = process.env.PORT || 3000;
const app = Express();
const renderer = () => {
  app.get("*", (req: Express.Request, res: Express.Response) => {
    res.send(`
			<!DOCTYPE HTML>
			<html>
				<head>
					<!-- insert metadata here -->
				</head>
				<body>
					<div id="app-root"></div>
				</body>
			</html>
		`);
  });
};

app.use(renderer);
app.listen(APP_PORT, () => {
  console.log(`Server is listening on ${APP_PORT}`);
});

